import time

# Function to retrieve pi up to 1000 digits
def get_pi():
    return ("3.")

def main():
    print("This program can display up to 1000 digits of pi.")
    while True:
        digits = input("Enter the number of digits of pi you want to view (up to 1000), "
                       "or enter 'q' to quit: ")

        if digits.lower() == 'q':
            print("Exiting in 10 seconds, Script created by KeiraOMG0, you can find her on Discord as keiraomg0 to contact her.")
            time.sleep(10)
            break

        try:
            digits = int(digits)
        except ValueError:
            print("Invalid input. Please enter a number or 'q' to quit.")
            continue
        
        if digits < 0:
            print("Please enter a non-negative number.")
            continue
        elif digits > 1000:
            print("The maximum number of digits allowed is 1000.")
            continue

        pi = get_pi()
        print(f"The first {digits} digits of pi are:\n{pi[:2 + digits]}")

if __name__ == "__main__":
    main()
